const BooksPage = () => {
  const { isLibrarian } = useAuth();
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingBook, setEditingBook] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [notification, setNotification] = useState(null);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const data = await apiRequest("/books");
      setBooks(data.content || []);
    } catch (error) {
      setNotification({ message: "Failed to fetch books", type: "error" });
    } finally {
      setLoading(false);
    }
  };

  const handleSaveBook = async (bookData) => {
    try {
      if (editingBook) {
        await apiRequest(`/books/${editingBook.id}`, {
          method: "PUT",
          body: JSON.stringify(bookData),
        });
        setNotification({
          message: "Book updated successfully!",
          type: "success",
        });
      } else {
        await apiRequest("/books", {
          method: "POST",
          body: JSON.stringify(bookData),
        });
        setNotification({
          message: "Book added successfully!",
          type: "success",
        });
      }

      fetchBooks();
      setShowAddModal(false);
      setEditingBook(null);
    } catch (error) {
      setNotification({ message: "Failed to save book", type: "error" });
    }
  };

  const handleDeleteBook = async (bookId) => {
    if (!window.confirm("Are you sure you want to delete this book?")) return;

    try {
      await apiRequest(`/books/${bookId}`, { method: "DELETE" });
      setNotification({
        message: "Book deleted successfully!",
        type: "success",
      });
      fetchBooks();
    } catch (error) {
      setNotification({ message: "Failed to delete book", type: "error" });
    }
  };

  const filteredBooks = books.filter(
    (book) =>
      book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      book.author.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
